import "./Log.css";
import React, { useRef, useState } from "react";
import { useAuth } from "../contexts/AuthContext";
import { Link, useHistory } from "react-router-dom";
export default function Log_sign() {
  const emailRef = useRef();
  const passwordRef = useRef();
  const passwordConfirmRef = useRef();
  const { login } = useAuth();
  const { signup } = useAuth();
  const [error, setError] = useState("Log In");
  const [error2, setError2] = useState("Sign Up");
  const [loading, setLoading] = useState(false);
  const history = useHistory();
  const [log, changelog] = useState(true);
  const [sign, changesign] = useState(false);
  const func1 = () => {
    changelog(true);
    changesign(false);
  };
  const func2 = () => {
    changesign(true);
    changelog(false);
  };

  async function handleSubmit(e) {
    e.preventDefault();

    try {
      setError("");
      setLoading(true);
      await login(emailRef.current.value, passwordRef.current.value);
      history.push("/");
    } catch {
      setError("Failed to log in");
    }

    setLoading(false);
  }

  async function handleSubmit2(e) {
    console.log("pps");
    e.preventDefault();

    if (passwordRef.current.value !== passwordConfirmRef.current.value) {
      return setError("Passwords do not match");
    }

    try {
      setError2("");
      setLoading(true);
      await signup(emailRef.current.value, passwordRef.current.value);
      history.push("/");
    } catch {
      setError2("Failed to create an account");
    }

    setLoading(false);
  }

  return (
    <div className="LogMain1">
      {log && (
        <div className="Login">
          <div className="logsubs">
            <div className="loghead">
              <p className="loginhead">{error}</p>
            </div>
            <Form onSubmit={handleSubmit} className="formlog">
              <div className="loginsub1">
                <div className="loginsub11">
                  <div className="ipdiv">
                    <div className="inputlog">
                      <input
                        className="inputlogip"
                        type="text"
                        ref={emailRef}
                        placeholder="Email"
                        required
                      />
                    </div>
                    <div className="inputlog">
                      <input
                        className="inputlogip"
                        type="password"
                        ref={passwordRef}
                        required
                        placeholder="Password"
                      />
                    </div>
                  </div>
                  <div className="inputlogbtn">
                    <Link
                      to={{ pathname: "/bestItem" }}
                      style={{ textDecoration: "none", color: "black" }}
                      className="inputlogbtn1"
                    >
                      <button
                        disabled={loading}
                        className="linkspace"
                        type="submit"
                      >
                        Log In
                      </button>
                    </Link>
                  </div>
                  <p className="su" onClick={func2}>
                    Forgot Password?
                  </p>
                </div>
              </div>
            </Form>
            <div className="loginsub2"></div>
          </div>
        </div>
      )}
      {!log && (
        <div className="Login2" onClick={func1}>
          <p className="largehead">Log In</p>
        </div>
      )}
      {sign && (
        <div className="Login">
          <div className="logsubs">
            <div className="loghead2">
              <p className="loginhead">{error2}</p>
            </div>
            <Form onSubmit={handleSubmit2} className="formsign">
              <div className="loginsub122">
                <div className="loginsub112">
                  <div className="ipdiv">
                    <div className="inputlog1">
                      <input
                        className="inputlogip"
                        type="text"
                        placeholder="Email"
                        ref={emailRef}
                        required
                      />
                    </div>
                    <div className="inputlog1">
                      <input
                        className="inputlogip"
                        type="password"
                        placeholder="Password"
                        ref={passwordRef}
                        required
                      />
                    </div>
                    <div className="inputlog1">
                      <input
                        className="inputlogip"
                        type="password"
                        placeholder="Confirm Password"
                        ref={passwordConfirmRef}
                        required
                      />
                    </div>
                  </div>
                  <div className="inputlogbtn2">
                    <Link
                      to={{ pathname: "/Items" }}
                      style={{ textDecoration: "none", color: "black" }}
                      className="inputlogbtn21"
                    >
                      <button
                        disabled={loading}
                        type="submit"
                        className="linkspace2"
                      >
                        Sign Up
                      </button>
                    </Link>
                  </div>
                </div>
              </div>
            </Form>
            <div className="loginsub2"></div>
          </div>
        </div>
      )}
      {!sign && (
        <div className="signup2" onClick={func2}>
          <p className="largehead">Sign Up</p>
        </div>
      )}
    </div>
  );
}
