import firebase from "./firebase";
import { useRef } from "react";
function Change() {
  const url = useRef();
  const handle = () => {
    firebase
      .database()
      .ref("Products")
      .on("value", (snapshot) => {
        const i = snapshot.val();
        for (let id in i) {
          firebase.database().ref(`Products/${id}/picture/1`).remove();
          firebase
            .database()
            .ref(`Products/${id}/picture/1`)
            .update({ url2: url.current.value });
        }
      });
  };
  return (
    <>
      <input type="url" ref={url} />
      <button onClick={handle}>Submit</button>
    </>
  );
}
export default Change;
