import img1 from "../photos/p1.jpg";
import img2 from "../photos/p2.jpg";
import img3 from "../photos/p3.jpg";
import img4 from "../photos/p4.jpg";
import Card from "./Card";
import "./Offer.css";
function Offer() {
  function displayfun() {
    <script>document.getElementById(id).style.display = flex;</script>;
  }
  function notdisplay() {
    <script>document.getElementById(id).style.display = none;</script>;
  }
  const imgs = [img1, img2, img3, img4];
  return (
    <div className="offer">
      {imgs.map((value, index) => {
        return (
          <Card id="id" delay={displayfun} delay2={notdisplay}>
            <img src={imgs[index % 4]} className="image"></img>
          </Card>
        );
      })}
    </div>
  );
}
export default Offer;
