import { useLocation } from "react-router-dom";
import Item from "./FilterItem";
import "../contents/Content.css";
import data from "../selectpage/Data3";
import Nav from "../navbar/Nav";

function Filter() {
  let i = 0;
  let j = 0;
  const properties = data.properties;
  let ind = useLocation();
  data.properties.map((index) => {
    if (
      index.color === ind.state.color &&
      index.cat === ind.state.cat &&
      index.name === ind.state.brand
    ) {
      if (
        index.size[0] === ind.state.siz ||
        index.size[1] === ind.state.siz ||
        index.size[2] === ind.state.siz ||
        index.size[3] === ind.state.siz
      ) {
        i = i + 1;
      }
    }
  });
  return (
    <div className="wrapper">
      {properties.map((value) => {
        if (
          data.properties[value.index].color === ind.state.color &&
          data.properties[value.index].cat === ind.state.cat &&
          data.properties[value.index].name === ind.state.brand
        ) {
          if (
            data.properties[value.index].size[0] === ind.state.siz ||
            data.properties[value.index].size[1] === ind.state.siz ||
            data.properties[value.index].size[2] === ind.state.siz ||
            data.properties[value.index].size[3] === ind.state.siz
          ) {
            if (i % 4 === 0) {
              j = j + 1;
              return (
                <Item
                  one={value.index}
                  two={value.index + 1}
                  three={value.index + 2}
                  four={value.index + 3}
                  sign={1}
                  notindex={-1}
                  key={data.properties[value.index]._id}
                />
              );
            } else if (i % 4 === 1) {
              if (j < i - 1) {
                j = j + 1;
                return (
                  <Item
                    one={value.index}
                    two={value.index + 1}
                    three={value.index + 2}
                    four={value.index + 3}
                    sign={1}
                    notindex={-1}
                    key={data.properties[value.index]._id}
                  />
                );
              } else {
                j = j + 1;
                return (
                  <Item
                    one={value.index}
                    sign={2}
                    notindex={-1}
                    key={data.properties[value.index]._id}
                  />
                );
              }
            } else if (i % 4 === 2) {
              if (j < i - 2) {
                j = j + 1;
                return (
                  <Item
                    one={value.index}
                    two={value.index + 1}
                    three={value.index + 2}
                    four={value.index + 3}
                    sign={1}
                    notindex={-1}
                    key={data.properties[value.index]._id}
                  />
                );
              } else {
                j = j + 1;
                return (
                  <Item
                    one={value.index}
                    two={value.index + 1}
                    sign={3}
                    notindex={-1}
                    key={data.properties[value.index]._id}
                  />
                );
              }
            } else if (i % 4 === 3) {
              if (j < i - 3) {
                j = j + 1;
                return (
                  <Item
                    one={value.index}
                    two={value.index + 1}
                    three={value.index + 2}
                    four={value.index + 3}
                    sign={1}
                    notindex={-1}
                    key={data.properties[value.index]._id}
                  />
                );
              } else {
                j = j + 1;
                return (
                  <Item
                    one={value.index}
                    two={value.index + 1}
                    three={value.index + 2}
                    sign={4}
                    notindex={-1}
                    key={data.properties[value.index]._id}
                  />
                );
              }
            }
          }
        }
      })}
    </div>
  );
}
export default Filter;
