import React from "react";
import { useRef, useState } from "react";
import firebase from "./firebase";
import "./Proinsert.css";
import { useAuth } from "./contexts/AuthContext";
import { useHistory } from "react-router-dom";
import { storage } from "./firebase";

export default function Proinsert() {
  const history = useHistory();
  const index = useRef();
  const cat = useRef();
  const pic1 = useRef();
  const pic2 = useRef();
  const pic3 = useRef();
  const pic4 = useRef();
  const name = useRef();
  const price = useRef();
  const size1 = useRef();
  const size2 = useRef();
  const size3 = useRef();
  const size4 = useRef();
  const size5 = useRef();
  const color = useRef();
  const desc = useRef();
  const cu = useAuth();
  const [img, changeimg] = useState();
  const [img2, changeimg2] = useState();
  const [img3, changeimg3] = useState();
  const [img4, changeimg4] = useState();
  const imgup = (e) => {
    changeimg(e.target.files[0]);
  };
  const imgup2 = (e) => {
    changeimg2(e.target.files[0]);
  };
  const imgup3 = (e) => {
    changeimg3(e.target.files[0]);
  };
  const imgup4 = (e) => {
    changeimg4(e.target.files[0]);
  };
  const handles = (e) => {
    e.preventDefault();
    storage
      .ref("images/${img.name}")
      .put(pic1.current.value)
      .on(
        "state_change",
        (snapshot) => {},
        (error) => {
          console.log(error);
        },
        () => {
          storage
            .ref("images")
            .child(img.name)
            .getDownloadURL()
            .then((url) => {
              console.log(url);
            });
        }
      );
    firebase
      .database()
      .ref("Products")
      .push({
        index: index.current.value,
        cat: cat.current.value,
        picture: [
          pic1.current.value,
          pic2.current.value,
          pic3.current.value,
          pic4.current.value,
        ],
        name: name.current.value,
        price: price.current.value,
        size: ["S", "M", "L", "XL", "XXL"],
        color: color.current.value,
        description: desc.current.value,
      });
    history.push("/Items");
  };
  return (
    <div className="promain">
      <form onSubmit={handles}>
        <input ref={index} type="number" />
        <input ref={cat} type="text" />
        <input ref={pic1} type="file" onChange={imgup} />
        <input ref={pic2} type="file" onChange={imgup2} />
        <input ref={pic3} type="file" onChange={imgup3} />
        <input ref={pic4} type="file" onChange={imgup4} />
        <input ref={name} type="text" />
        <input ref={price} type="number" />
        <input ref={color} type="text" />
        <textarea ref={desc} type="text" />
        <button type="submit">Submit</button>
      </form>
    </div>
  );
}
