import React, { Component } from "react";
import { render } from "@testing-library/react";
import imgs from "./data";
import Card from "./Card";
import Left from "@material-ui/icons/ArrowBackIosRounded";
import Right from "@material-ui/icons/ArrowForwardIosRounded";
import "./Card.css";
import "./Offer.css";
class Offer extends Component {
  constructor(props) {
    super(props);
    this.state = {
      properties: imgs.properties,
      property: imgs.properties[0],
    };
  }
  nextProperty = () => {
    const newIndex = this.state.property.index + 1;
    this.setState({
      property: imgs.properties[newIndex],
    });
  };
  prevProperty = () => {
    const newIndex = this.state.property.index - 1;
    this.setState({
      property: imgs.properties[newIndex],
    });
  };
  render() {
    const { properties, property } = this.state;
    return (
      <div className="offer">
        <div id={"card-${property.index}"} className="card">
          <button
            className="btn"
            onClick={() => this.prevProperty()}
            disabled={property.index === 0}
          >
            <Left style={{ fonSize: 20 }} />
          </button>

          <img src={property.picture}></img>

          <button
            className="btn"
            onClick={() => this.nextProperty()}
            disabled={property.index === imgs.properties.length - 1}
          >
            <Right />
          </button>
        </div>
      </div>
    );
  }
}
export default Offer;
