import React from "react";
import firebase from "./firebase";
export default function Proinsert() {
  const handle = () => {
    firebase
      .database()
      .ref("Products")
      .push({
        index: index,
        cat: cat,
        picture: [pic1, pic2, pic3, pic4],
        name: name,
        price: price,
        size: size1,
        color: color,
        description: description,
      });
  };
  return (
    <div>
      <form onSubmit={handle}>
        <input value={index} type="number" />
        <input value={cat} type="text" />
        <input value={pic1} type="image" />
        <input value={pic2} type="image" />
        <input value={pic3} type="image" />
        <input value={pic4} type="image" />
        <input value={name} type="text" />
        <input value={price} type="number" />
        <input value={size1} type="number" />
        <input value={color} type="text" />
        <textarea value={description} type="text" />
        <button type="submit">Submit</button>
      </form>
    </div>
  );
}
