import "../Mainselect.scss";
import data from "./Data3";
import data2 from "../contents/Data2";
import React from "react";
import ReactDOM from "react-dom";
import { useState, useEffect } from "react";
let f = 1;
let a = 0,
  b = 0,
  c = 0,
  d = 0,
  e = 0,
  t = 0;
function Selitem(props) {
  if (t !== props.index) {
    f = 1;
    t = props.index;
  }
  const [btn, changebtn] = useState(1);
  const btnfucn = () => {
    changebtn(0);
  };
  const [two, changeTwo] = useState(false);
  if (f === 1) {
    f = f + 1;
    d = props.index * 4;
    data.properties.map((index) => {
      if (props.index === index.index) {
        if (e === 0) {
          a = index._id;
          e = e + 1;
        } else if (e === 1) {
          b = a + 1;
          e = e + 1;
        } else if (e === 2) {
          c = a + 2;
          e = 0;
        }
      }
    });
  }
  const imgclick1 = () => {
    e = d;
    d = a;
    a = e;
    e = 0;

    changeTwo(!two);
  };
  const imgclick2 = () => {
    e = d;
    d = b;
    b = e;
    e = 0;
    changeTwo(!two);
  };
  const imgclick3 = () => {
    e = d;
    d = c;
    c = e;
    e = 0;
    changeTwo(!two);
  };
  return (
    <div className="class2">
      <div className="angle">
        <div className="angle1">
          <img
            className="angle2 angle21"
            src={data.properties[a].picture}
            alt="Loading..."
            onClick={imgclick1}
          />
        </div>
        <div className="angle1">
          <img
            className="angle2"
            src={data.properties[b].picture}
            alt="Loading..."
            onClick={imgclick2}
          />
        </div>
        <div className="angle1">
          <img
            className="angle2"
            src={data.properties[c].picture}
            alt="Loading..."
            onClick={imgclick3}
          />
        </div>
      </div>
      <div className="mainitem">
        <div className="Mainimg">
          <img src={data.properties[d].picture} className="Mainimg2" />
        </div>
      </div>
      <div className="descrp2" style="--btn:btn;">
        <p className="txt3">{data.properties[props.index].name}</p>
        <p className="txt2">{data.properties[props.index].cat} Category</p>
        <p className="txt2">Rs.{data.properties[props.index].price}/-</p>
        <button className="btn3" onClickCapture={btnfucn}>
          Details
        </button>
        <br />
        <button className="btn1">Add To Cart</button>
        <br />
        <button className="btn2">Buy</button>
      </div>
    </div>
  );
}
export default Selitem;
