import Content from "../contents/Content";
import Nav from "../navbar/Nav";
import "./Order.css";
import firebase from "../firebase";
import { Link, useHistory } from "react-router-dom";
import { useAuth } from "../contexts/AuthContext";
function Order() {
  const cu = useAuth();
  const properties = [];
  firebase
    .database()
    .ref("User")
    .on("value", (snapshot) => {
      const i = snapshot.val();
      console.log(i);
      for (let id in i) {
        if (i[id].email === cu.currentUser.email) {
          firebase
            .database()
            .ref(`User/${id}/orders`)
            .on("value", (snapshot) => {
              let j = snapshot.val();
              for (let y in j) {
                properties.push(j[y]);
              }
            });
        }
      }
    });
  return (
    <>
      <Nav />
      <>
        {properties.map((index) => {
          return (
            <div className="ordercardmain1">
              <div className="ordercardmain01">
                <div className="ordercardmain2">
                  <div className="ordernum">
                    <p className="orderheadname2"></p>
                  </div>

                  <div className="ordercardmain3">
                    <p className="orderheadname"></p>
                    <div className="ordersubcard1">
                      <div className="orderip">
                        <p>{index.Name}</p>
                      </div>
                      <div className="orderip">
                        <p>{index.City}</p>
                      </div>
                      <div className="orderip">
                        <p>{index.State}</p>
                      </div>
                      <div className="orderip">
                        <p>{index.email}</p>
                      </div>
                      <div className="orderip">
                        <p>{index.Mobile}</p>
                      </div>
                      <div className="orderip">
                        <p>Rs.{index.total}/-</p>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          );
        })}
      </>
      <Content notindex={-1} />
    </>
  );
}
export default Order;
