import img1 from "../photos/p1.jpg";
import img2 from "../photos/p2.jpg";
import img3 from "../photos/p3.jpg";
import img4 from "../photos/p4.jpg";
import Card from "./Card";
import "./Offer.css";
function Offer() {
  const imgs = [img1, img2, img3, img4];
  let p = 0;
  return (
    <div className="offer">
      <Card>
        <img
          src={imgs.map((ph) => {
            p % 3, (p = p + 1);
          })}
          className="image"
        ></img>
      </Card>
    </div>
  );
}
export default Offer;
