import React, { Component, useState } from "react";
import { render } from "@testing-library/react";
import imgs from "./data";
import Card from "./Card";
import Left from "@material-ui/icons/ArrowBackIosRounded";
import Right from "@material-ui/icons/ArrowForwardIosRounded";
import "./Card.css";
import "./Test.css";
function Test() {
  const [indexNext, indexChange] = useState(0);

  const nextProperty = () => {
    indexChange(indexNext + 1);
  };
  const prevProperty = () => {
    indexChange(indexNext - 1);
  };
  return (
    <div className="offer">
      <button
        className="btn"
        onClick={() => prevProperty()}
        disabled={indexNext === 0}
      >
        <Left id="si2" />
      </button>

      <div className="slider-wrapper">
        <div className="slider">
          {imgs.properties.map((index) => (
            <Card property={index.index} animat={nextProperty} />
          ))}
        </div>
      </div>
      <button
        className="btn"
        onClick={() => nextProperty()}
        disabled={indexNext === imgs.properties.length - 1}
      >
        <Right id="si3" />
      </button>
    </div>
  );
}
export default Test;
